 namespace Optimization\Critical\CSS\Data; class Property { protected ?string $name = ''; protected ?string $value = ''; public function __construct(?string $name, ?string $value) { $this->name = $name; $this->value = $value; } public function getName() : ?string { return $this->name; } public function getValue() : ?string { return $this->value; } public function isImportant() { return stripos($this->getValue(), "\x21\151\155\160\x6f\162\164\x61\156\164") !== false; } public function __toString() { return sprintf("\45\x31\x24\163\72\40\45\x32\44\163\x3b", $this->getName(), $this->getValue()); } }
